mapper.select:
SELECT pnr.id as id, pnr.namespace_id AS namespace_id, pnr.config AS config, pnr.sort AS sort, pnr.date_created AS date_created, pnr.date_updated AS date_updated, p.role AS role, p.name AS name, p.plugin_jar AS plugin_jar FROM ( SELECT
FROM plugin_ns_rel
AND name LIKE #{nameLike, jdbcType=VARCHAR}
AND enabled = #{enabled, jdbcType=TINYINT}
AND namespace_id = #{namespaceId, jdbcType=TINYINT}
) pnr LEFT JOIN PLUGIN p ON p.id = pnr.plugin_id ORDER BY sort, pnr.id ASC

SELECT
pnr.namespace_id AS namespace_id,
pnr.config AS config,
pnr.sort AS sort,
pnr.date_created AS date_created,
pnr.date_updated AS date_updated,
p.role AS role,
p.NAME AS NAME,
p.plugin_jar AS plugin_jar
FROM plugin_ns_rel pnr LEFT JOIN plugin p ON pnr.plugin_id = p.id



        AND (
        p.name LIKE #{keyword, jdbcType=VARCHAR}
        OR  p.role LIKE #{keyword, jdbcType=VARCHAR}
        )



        AND (
        p.name NOT LIKE #{excluded, jdbcType=VARCHAR}
        OR  p.role NOT LIKE #{excluded, jdbcType=VARCHAR}
        )


        AND  pnr.enabled = #{condition.switchStatus, jdbcType=BOOLEAN}


ORDER BY pnr.sort, pnr.date_created
